#include<OneKeyD2D/OneKeyD2D.hpp> using namespace Game; void PaintWindow(MainWind_D2D* window)
{ //绘制一个正五边形 POINT* point = (POINT*)window->GetUserData(); POINT points[5]; float angle = 3.14159 * 2 / 5; float size = 100; for (int i = 0; i < 5; ++i) { points[i] = { (int)(point->x + size * std::sin(angle * i)),(int)(point->y - size * std::cos(angle * i)) }; } window->DrawGeometry(points, 5);
} void KeyInputCallback(MainWind_D2D* window, int key, int, KeyMode mode)
{ //只解析字符消息 if (mode == KM_CHAR) { //获取基点 POINT* point = (POINT*)window->GetUserData(); //单次移动距离 int speed = 10; switch (key) { //w键向上移动 case 'w': { point->y -= speed; break; } case 's': { //s键向下移动 point->y += speed; break; } case 'a': { //a键左移 point->x -= speed; break; } case 'd': { //d键右移 point->x += speed; break; } default: break; } window->PaintWind(); }
}
int main()
{ //图像基准点 POINT point = { 100,100 }; //窗口索引标识 MainWind_D2D Window; //创建窗口 Window.CreateWind(); //将数据指针作为用户数据绑定 Window.SetUserData((long long)&point); //设置背景 Window.AutoClearWindBackground(RGB(200,200,0)); //设置绘图回调 Window.SetPaintCallback(PaintWindow); //设置按键回调 Window.SetKeyCallback(KeyInputCallback); //开启消息循环 Window.StaticWindowMessageLoop(); return 0;
}

